//消息提醒
var flash={
    flag:false,
    isMin:false,
    time:false,
    flash_title:function () {
        //当窗口效果为最小化，或者没焦点状态下才闪动
        var that=this;
        if(that.isMin) {
            that.newMsgCount();
        } else {
            //window.clearInterval();
            if(that.time){
                window.clearInterval(that.time);
                that.time=false;
            }
            document.title='我信_我连网';//窗口没有消息的时候默认的title内容
        }
    },
    newMsgCount:function (){
        if(this.flag){
            this.flag=false;
            document.title='【新消息】';
        }else{
            this.flag=true;
            document.title='【　　　】';
        }
        if(this.time ==false){
            this.time=window.setInterval('flash.flash_title(0)',380);
        }

    }
};
var hiddenProperty = 'hidden' in document ? 'hidden' :
    'webkitHidden' in document ? 'webkitHidden' :
        'mozHidden' in document ? 'mozHidden' :
            null;
var visibilityChangeEvent = hiddenProperty.replace(/hidden/i, 'visibilitychange');
var onVisibilityChange = function(){
    if (!document[hiddenProperty]) {
        flash.isMin=false;
    }else{
        flash.isMin=true;
    }
};
document.addEventListener(visibilityChangeEvent, onVisibilityChange);
function getMsgId(id){
    return id+'_'+new Date().getTime();
}

var tips={
    isShown:false,///是否显示
    content:'',//显示内容
    hideTime:null,//延时隐藏设定
    option:{
        auto:true,///自动隐藏
        click:true,///点击隐藏
        time:1000//显示时间
    },
    show:function(content,option){
        var that=this;
        that.content=content;
        var init={
            auto:true,///自动隐藏
            click:true,///点击隐藏
            time:1000//显示时间
        };
        if(option){
            $.extend(init,option);
        }
        $.extend(that.option,init);
        if(that.isShown){
            $('div.tips_content').html(content);
        }else {
            if(content){
                $('body').append('<div class="tips"><div class="tips_content">'+content+'</div></div>');
            }
        }
        that.isShown=true;
        var tipsDOM=$('.tips');
        tipsDOM.animateCss("fadeIn",'show');
        if(that.option.click){
            tipsDOM.show().find('.tips_content').unbind('click').click(function(){
                that.hide()
            });
        }else {
            tipsDOM.show().find('.tips_content').unbind('click');
        }
        tipsDOM.find('.tips_content').animateCss('fadeIn',function(){
            if(that.option.auto){
                if(that.hideTime){
                    clearTimeout(that.hideTime);
                }
                that.hideTime=setTimeout(function(){
                    that.hide();
                },that.option.time);
            }
        });

    },
    hide:function(){
        var self=this;
        //if(self.isShown){
            $('.tips').animateCss('fadeOut','remove');
        //}
        self.isShown=false;
    },
    loading:function(){
        var load='<img src="/img/loadingFile.gif" /><p>Loading</p>';
        this.show(load,{
            auto:false,///自动隐藏
            click:true///点击隐藏
        });
    }
};
///加载视频出错
function videoError(elem){
    $(elem).parent().html('<img src="'+_errorVideo+'" />');
}
//获取文件名
function getFileName(path){
    var pos1 = path.lastIndexOf('/');
    var pos2 = path.lastIndexOf('\\');
    var pos  = Math.max(pos1, pos2);
    if( pos < 0 )
        return path;
    else
        return path.substring(pos+1);
}
///dataURL转换为Blob对象
function dataURLtoBlob(dataurl) {
    var arr = dataurl.split(','), mime = arr[0].match(/:(.*?);/)[1],
        bstr = atob(arr[1]), n = bstr.length, u8arr = new Uint8Array(n);
    while(n--){
        u8arr[n] = bstr.charCodeAt(n);
    }
    return new Blob([u8arr], {type:mime});
}

function getParam(url,key){
    if(url.indexOf('?')==-1){
        return false;
    }
    var obj={};
    var params=url.split('?')[1];
    var arr=false;
    if(params.indexOf('&')==-1){
        arr=[params];
    }else {
        arr=params.split('&');
    }
    for (var i=0;i<arr.length;i++){
        var temp=arr[i].split('=');
        obj[temp[0]]=temp[1];
    }
    if(key && (typeof key ==='string')){
        return obj[key];
    }
    return obj;
}